#!/bin/bash

# Kubernetes Dashboard Installation Script
# Run this AFTER your initial backend deployment

set -e

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m'

print_status() {
    echo -e "${BLUE}[INFO]${NC} $1"
}

print_success() {
    echo -e "${GREEN}[SUCCESS]${NC} $1"
}

print_warning() {
    echo -e "${YELLOW}[WARNING]${NC} $1"
}

print_error() {
    echo -e "${RED}[ERROR]${NC} $1"
}

print_status "🎛️ Installing Kubernetes Dashboard..."

# Step 1: Install the official Kubernetes Dashboard
print_status "Step 1: Installing official Kubernetes Dashboard..."
kubectl apply -f https://raw.githubusercontent.com/kubernetes/dashboard/v2.7.0/aio/deploy/recommended.yaml

# Step 2: Apply our custom configuration
print_status "Step 2: Applying custom dashboard configuration..."
kubectl apply -f k8s/dashboard-setup.yaml

# Step 3: Wait for dashboard to be ready
print_status "Step 3: Waiting for dashboard to be ready..."
kubectl wait --for=condition=available deployment/kubernetes-dashboard -n kubernetes-dashboard --timeout=300s

print_success "Dashboard deployment is ready!"

# Step 4: Get the admin token
print_status "Step 4: Creating admin token..."
kubectl -n kubernetes-dashboard create token admin-user > dashboard-token.txt

print_success "Admin token created and saved to dashboard-token.txt"

# Step 5: Get access information
print_status "Step 5: Getting access information..."

# Get LoadBalancer IP (may take a few minutes)
print_status "Waiting for LoadBalancer IP (this may take 2-3 minutes)..."
kubectl get service kubernetes-dashboard-external -n kubernetes-dashboard --watch --timeout=300s

DASHBOARD_IP=$(kubectl get service kubernetes-dashboard-external -n kubernetes-dashboard -o jsonpath='{.status.loadBalancer.ingress[0].ip}' 2>/dev/null || echo "pending")

echo ""
print_success "🎉 Kubernetes Dashboard installation completed!"

echo ""
